 INSERT INTO ESTMVVD5 (vd5_cod, vd5_bol, vd5_ent, vd5_tip, vd5_num, vd5_cop, vd5_cep, vd5_end, vd5_bai, vd5_cid, vd5_est, vd5_ref, vd5_alm, vd5_cad, vd5_nom, vd5_cnp, vd5_ies, vd5_fis, vd5_tipent, vd5_pais, vd5_prov)
