 if ((vProAnt <> vProCod) or (Coalesce(vCorAnt, '') <> Coalesce(vVd2Cor, '')) or (Coalesce(vMedAnt, '') <> Coalesce(vVd2Med, '')) or (vAlmAnt <> vVd2Alm)) then
 begin
