 if (vVd1Ori = 'CNT') then
 begin
 vSaldo = vQuantidadeMovimentada;
 if ((vCompCont <> 1) or (vDatAux <> vData)) then
 vSaldo_Sem_Contagem = vSaldo;
 end
 else
 begin
 vSaldo = vQuantidadeMovimentada + vSaldo;
 vSaldo_Sem_Contagem = vSaldo;
 end
 end
