 null, null, null, null, null, null,
 null, null, null, null, null, null,
 null, null, '', 0, 1)
 Into TOT_AJST;
 
 SOMA = EST_ANT + TOT_MOV + TOT_AJST;
 
 DIFERENCA = SOMA - EST_FIN;
 if ((PSODIFERENCA <> 1) or (DIFERENCA <> 0)) then
