 from dur_est_grupo(:pTipo, :pdatade, :pdataate, :pprode, :pproate, :palmde, :palmate,
 :pmarde, :pmarate, :pgrude, :pgruate, :psgrude, :psgruate, :ptipde, :ptipate,
 :ptrade, :ptraate, :pcatde, :pcatate, :pusacor, :pusamedida)
