    PVALOR DOUBLE PRECISION)
AS
DECLARE VARIABLE VCODIGO VARCHAR(13);
DECLARE VARIABLE VNUMERO INTEGER;
DECLARE VARIABLE VCPF VARCHAR(19);
DECLARE VARIABLE VTIPCLI VARCHAR(1);
DECLARE VARIABLE VFATNUM VARCHAR(10);
