    PFILTRONIVEL INTEGER)
RETURNS (
    PRO_PAI INTEGER,
    PRO_NIV VARCHAR(1000),
    PRO_COD INTEGER,
    PRO_NOM VARCHAR(100),
    PRO_INT VARCHAR(15),
    PRO_BAR VARCHAR(15),
    PRO_QTD DOUBLE PRECISION,
    PRO_VLR DOUBLE PRECISION,
    PRO_SEQ INTEGER,
    UNI_COD INTEGER,
    UNI_NOM VARCHAR(30),
    UNI_SIG VARCHAR(5),
    EST_SAL DOUBLE PRECISION)
AS
 declare variable vprocod integer;
 declare variable vproqtd double precision;
