 begin
 select
 sum((vd2_vlr - (coalesce(descontoitem * vd2_vlr / 100.00,0))) * vd10_qtd *
 case when (v1.vd1_valor_totalb > 0) then (v1.vd1_valor_totall - :ABATIMENTO)/ v1.vd1_valor_totalb else 0 end *
