 Insert Into estmvvd10 (vd10_cod, vd10_vd9, vd10_pro, vd10_cor, vd10_med, vd10_qtd, vd10_ser)
 Select (Select max(vd10_cod)+1 from estmvvd10), vd9_cod, vd2_pro, vd2_cor, vd2_med, vd2_qtd, 0
