 DECLARE VARIABLE VPERCENT DOUBLE PRECISION;
 DECLARE VARIABLE VNPARC INTEGER;
 begin
 pSaldo = 0;
 pQuant = 0;
 For Select
 vd1_canc,
 estmvvd1.vd1_total_nota_semdescto,
 VD1_DESC_V,
 VD1_DESC_P,
 vd2_vlr,
 vd2_qtd,
 descontoitem,
