 and vd1_alm = vd2_alm
 and vd1_cad = vd2_cad
 Into vVd1Can, vVd1Vtp, vVd1DsV, vVd1DsP, vVd2Vlr, vVd2Qtd, vVd2Dsc, vVd10Pd Do
 begin
 
 Select Saldo from SALDO_DEVOLUCOES(:PVD1COD) into vCred;
 if (vCred is Null) then
 vCred = 0;
 vVd1DsV = vVd1DsV+vCred;
 if (vVd2Qtd is Null) then
