 CASE WHEN :WLAYOUTPDV = 'SER' THEN :WTOTBRU ELSE 0 END, CASE WHEN :WLAYOUTPDV = 'SER' THEN :WTOTLIQ ELSE 0 END, CASE WHEN :WLAYOUTPDV = 'SER' THEN :WQTDVD2 ELSE 0 END,
