ALTER PROCEDURE GERA_CDPROPRE (
    PPRODUTO INTEGER,
    PATIVO VARCHAR(1),
    PMARGEM DOUBLE PRECISION,
    PDESCONTO DOUBLE PRECISION,
    PVAL_DE TIMESTAMP,
    PVAL_ATE TIMESTAMP,
    PCOMISSAO DOUBLE PRECISION)
RETURNS (
    RESULT VARCHAR(1))
AS
