 Where cdprocusto.pro_pro = :PPRODUTO
 Order by cdprocusto.pro_seq, cdpro.pro_nom
 Into PRO_COD, PRO_NOM, PRO_INT, PRO_BAR, PRO_QTD, PRO_VLR, PRO_SEQ,
 UNI_COD, UNI_NOM, UNI_SIG, EST_SAL do
