 WHERE BAI_CLI=:PCODCLI
 GROUP BY BAI_PGT
 ORDER BY 1
 INTO VDATA, VRECEBER, VPAGAR DO
 
 BEGIN
 ACUM = ACUM + (VRECEBER-VPAGAR);
 DATA = VDATA;
 SUSPEND;
 END
 END
 ELSE IF (PTIPO=1) THEN
